import React from "react";
import "./YouTubeForm.css";
import { useForm, SubmitHandler } from "react-hook-form";
import { DevTool } from "@hookform/devtools";

export default function YouTubeForm() {
  const {
    register,
    handleSubmit,
    watch,
    control,
    formState: { errors },
  } = useForm();

  const onsubmit = (data) => {
    console.log(data);
  };

  return (
    <div className="main">
      <h1>login form</h1>
      <h3>Enter your login credentials</h3>

      <form action="" method="post" onSubmit={handleSubmit(onsubmit)}>
        <label htmlFor="username">Username:</label>
        <input className={`${errors.username.message}redInput`}
          type="text"
          id="first"
          name="first"
          placeholder="Enter your Username"
          {...register("username", {
            required: "this field is required",
            pattern:{
               value:  /^(?=.*[A-Za-z\d].*[A-Za-z\d])(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{2,8}$/ ,
               message:'this username is ghalat koskesh'
            }
              
          })}
        />
        <p>{errors.username?.message}</p>

        <label htmlFor="password">Password:</label>
        <input className="redInput"
          type="password"
          id="password"
          name="password"
          placeholder="Enter your Password"
          {...register("password", {
            required: "this field is required",
            pattern:{
                  value:/^(?=.*[0-9])(?=.*[a-zA-Z])(?=.*[@$!%*?&])[a-zA-Z0-9@$!%*?&]{2,13}$/,
                  message:'this password in not valid'
            }
              
          })}
        />
 <p>{errors.password?.message}</p>
        <div className="wrap">
          <button type="submit">Submit</button>
        </div>
      </form>

      <DevTool control={control} />
      <p>
        Not registered?{" "}
        <a href="#" style={{ textDecoration: "none" }}>
          Create an account
        </a>
      </p>
    </div>
  );
}
