import React, { useReducer, useState } from "react";

// import inputRegexValidator from './../../validators'

import "./Input.css";

export default function Input(props) {

  const initialInputSt={
    value:'',
    checkRegex:null
  }

  const inputStReducer= (state, action)=>{
    switch(action.type){
      case "CHANGE" : {
          return {...state, value:action.value, checkRegex:action.checkRegex}
      }
      default:{
        return state
      }
    }
  }

  const [inputSt, dispatch]=useReducer(inputStReducer, initialInputSt)

  const onChangeHandler = (event) => {
    dispatch({
      type:"CHANGE",
      value:event.target.value,
      checkRegex:props.inputRegexValidator(inputSt.value)
    })
  }



  const element =
    props.element === "input" ? (
      <input
        type={props.type}
        placeholder={props.placeholder}
        className={`${props.className} ${
         inputSt.checkRegex ? "success" : "error"
        }`}
        value={inputSt.value}
        onChange={onChangeHandler}
      />
    ) : (
      <textarea
        placeholder={props.placeholder}
        // className={`${props.className} ${
        //   mainInput.isValid ? "success" : "error"
        // }`}
        onChange={onChangeHandler}
        // value={mainInput.value}
      />
    );

  return <div>{element}</div>;
}
