import React, { useReducer, useState } from "react";

// import inputRegexValidator from './../../validators'

import "./Input.css";

export default function Input(props) {

  const initialInputSt={
    value:'',
    checkRegex1:null,
    checkRegex2:null
  }

  const inputStReducer= (state, action)=>{
    switch(action.type){
      case "CHANGE" : {
          return {...state, value:action.value, checkRegex1:action.checkRegex1, checkRegex2:action.checkRegex2}
      }
      default:{
        return state
      }
    }
  }

  const [inputSt, dispatch]=useReducer(inputStReducer, initialInputSt)

  const onChangeHandler = (event) => {
    dispatch({
      type:"CHANGE",
      value:event.target.value,
      checkRegex1:props.inputRegexValidator(inputSt.value),
      checkRegex2:props.inputRegexValidator(inputSt.value),
    })
  }





  const element =
    props.element === "input" ? (
      <input
        type={props.type}
        placeholder={props.placeholder}
        className={`${props.className} ${
         inputSt.checkRegex1 ? "success" : "error"
        }`}
        value={inputSt.value}
        onChange={onChangeHandler}
      />
    ) : (
      <textarea
        placeholder={props.placeholder}
        className={`${props.className} ${
          inputSt.checkRegex2.isValid ? "success" : "error"
        }`}
        onChange={onChangeHandler}
        value={inputSt.value}
      />
    );

  return <div>{element}</div>;
}
